import React, { useState, useEffect } from "react";

export default function Home() {
  const [showEmergency, setShowEmergency] = useState(false);
  const [showKupot, setShowKupot] = useState(false);
  const [showTaxi, setShowTaxi] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [largeText, setLargeText] = useState(false);
  const [clock, setClock] = useState("");

  useEffect(() => {
    const savedPrefs = JSON.parse(localStorage.getItem("prefs"));
    if (savedPrefs) {
      setDarkMode(savedPrefs.darkMode);
      setLargeText(savedPrefs.largeText);
    }

    const updateClock = () => {
      const now = new Date();
      const time = now.toLocaleTimeString("he-IL", { hour: "2-digit", minute: "2-digit" });
      const date = now.toLocaleDateString("he-IL", { weekday: "long", year: "numeric", month: "long", day: "numeric" });
      setClock(`${date} | ${time}`);
    };

    updateClock();
    const interval = setInterval(updateClock, 60000);
    return () => clearInterval(interval);
  }, []);

  const saveSettings = () => {
    localStorage.setItem("prefs", JSON.stringify({ darkMode, largeText }));
    setShowSettings(false);
  };

  const kupot = [
    { name: "כללית", url: "https://e-services.clalit.co.il/onlinewebquick/%D7%96%D7%9E%D7%9F_%D7%AA%D7%95%D7%A8" },
    { name: "מכבי", url: "https://www.maccabi4u.co.il/14-he/Maccabi.aspx" },
    { name: "לאומית", url: "https://home.leumit.co.il/" }
  ];

  const taxiApps = [
    { name: "Gett", url: "https://gett.com/il/" },
    { name: "Yango", url: "https://yango.com/he_il/" }
  ];

  const baseClasses = "min-h-screen flex flex-col items-center justify-start px-4 py-8 space-y-6 text-right";
  const themeClasses = darkMode ? "bg-zinc-900 text-white" : "bg-white text-gray-800";
  const textSize = largeText ? "text-xl" : "text-base";
  const mainClass = `${baseClasses} ${themeClasses} ${textSize}`;

  const cardStyle = "w-full rounded-xl border border-gray-300 hover:border-blue-500 shadow-sm p-5 text-center font-semibold";
  const menuBox = "absolute top-full right-0 mt-2 bg-white border rounded-xl shadow-md w-full z-10 text-right";
  const btnBase = "w-full rounded-xl py-4 px-2 text-lg md:text-xl font-semibold";

  return (
    <div className={mainClass}>
      <div className="text-sm text-gray-500">{clock}</div>

      <h1 className="text-3xl md:text-4xl font-bold text-blue-800">ברוכים הבאים ל"נכד" 👋</h1>

      <nav className="w-full max-w-md flex justify-around text-sm underline text-blue-700">
        <a href="#services">שירותים</a>
        <a href="#help">עזרה</a>
      </nav>

      <div id="services" className="grid sm:grid-cols-1 md:grid-cols-2 gap-4 w-full max-w-md text-center mt-4">
        <div className="relative col-span-1">
          <button
            onClick={() => setShowEmergency(!showEmergency)}
            className={`${btnBase} bg-red-100 text-red-800 hover:bg-red-200 border border-red-300`}
          >
            🚑 חירום
          </button>
          {showEmergency && (
            <div className={menuBox}>
              <a href="tel:100" className="block px-4 py-2 hover:bg-gray-100">🚔 משטרה - 100</a>
              <a href="tel:101" className="block px-4 py-2 hover:bg-gray-100">🚑 מד"א - 101</a>
              <a href="tel:102" className="block px-4 py-2 hover:bg-gray-100">🔥 כיבוי אש - 102</a>
            </div>
          )}
        </div>

        <div className="relative col-span-1">
          <button
            onClick={() => setShowKupot(!showKupot)}
            className={`${btnBase} bg-green-100 text-green-800 hover:bg-green-200 border border-green-300`}
          >
            🩺 קבע תור לרופא
          </button>
          {showKupot && (
            <div className={menuBox}>
              {kupot.map((kupa, idx) => (
                <a key={idx} href={kupa.url} target="_blank" rel="noopener noreferrer" className="block px-4 py-2 hover:bg-gray-100">
                  🏥 {kupa.name}
                </a>
              ))}
            </div>
          )}
        </div>

        <div className="relative col-span-1">
          <button
            onClick={() => setShowTaxi(!showTaxi)}
            className={`${btnBase} bg-yellow-100 text-yellow-800 hover:bg-yellow-200 border border-yellow-300`}
          >
            🚖 הזמן מונית
          </button>
          {showTaxi && (
            <div className={menuBox}>
              {taxiApps.map((app, idx) => (
                <a key={idx} href={app.url} target="_blank" rel="noopener noreferrer" className="block px-4 py-2 hover:bg-gray-100">
                  🚕 {app.name}
                </a>
              ))}
            </div>
          )}
        </div>
      </div>

      <div id="help" className="w-full max-w-md bg-gray-50 border border-gray-300 rounded-xl p-4 shadow text-right mt-6">
        <h2 className="text-xl font-semibold mb-2 text-pink-700">🆘 עזרה</h2>
        <ul className="list-disc pr-5 space-y-1 text-sm">
          <li>לחצו על כפתורי השירותים – כל כפתור מוביל לאתר או פעולה.</li>
          <li>השתמשו בהגדרות כדי להגדיל טקסט או להדליק מצב כהה.</li>
          <li>אם עדיין קשה, בקשו עזרה מקרוב משפחה.</li>
        </ul>
      </div>

      <button
        onClick={() => setShowSettings(true)}
        className="mt-10 underline text-blue-700 text-sm"
      >
        ⚙️ הגדרות נגישות
      </button>

      {showSettings && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white p-6 rounded-xl w-full sm:max-w-sm md:max-w-md text-right space-y-4 shadow-lg">
            <h2 className="text-xl font-bold">⚙️ הגדרות נגישות</h2>
            <div className="space-y-2">
              <label className="flex items-center space-x-2">
                <input type="checkbox" checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
                <span>מצב כהה</span>
              </label>
              <label className="flex items-center space-x-2">
                <input type="checkbox" checked={largeText} onChange={() => setLargeText(!largeText)} />
                <span>הגדלת טקסט</span>
              </label>
            </div>
            <div className="flex justify-end gap-2">
              <button onClick={() => setShowSettings(false)} className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400">ביטול</button>
              <button onClick={saveSettings} className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700">שמור</button>
            </div>
          </div>
        </div>
      )}

      <p className="text-gray-400 text-xs mt-8">שירות פשוט. בעברית. בשבילך.</p>
    </div>
  );
}
