import React, { useState, useEffect } from "react";

// ===== Inline SVG Icons (iOS-style) =====
const IconSiren = () => (
  <svg viewBox="0 0 24 24" className="w-8 h-8" fill="none" stroke="currentColor" strokeWidth="1.8">
    <path d="M7 10a5 5 0 1 1 10 0v4" />
    <rect x="4" y="14" width="16" height="6" rx="2" className="fill-current" />
    <path d="M12 2v3M4 7l2 2M20 7l-2 2M2 12h3M22 12h-3" />
  </svg>
);
const IconStetho = () => (
  <svg viewBox="0 0 24 24" className="w-8 h-8" fill="none" stroke="currentColor" strokeWidth="1.8">
    <path d="M6 3v6a4 4 0 1 0 8 0V3" />
    <path d="M14 17a4 4 0 0 0 8 0v-1a2 2 0 0 0-2-2h-1" />
    <circle cx="6" cy="3" r="1" className="fill-current" />
  </svg>
);
const IconTaxi = () => (
  <svg viewBox="0 0 24 24" className="w-8 h-8" fill="none" stroke="currentColor" strokeWidth="1.8">
    <path d="M3 12h18l-1.5-4.5A2 2 0 0 0 17.6 6H6.4a2 2 0 0 0-1.9 1.5L3 12Z" />
    <path d="M5 12v5a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2v-5" />
    <circle cx="7.5" cy="17" r="1.5" className="fill-current" />
    <circle cx="16.5" cy="17" r="1.5" className="fill-current" />
  </svg>
);

export default function Home() {
  const [showEmergency, setShowEmergency] = useState(false);
  const [showKupot, setShowKupot] = useState(false);
  const [showTaxi, setShowTaxi] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [largeText, setLargeText] = useState(false);
  const [clock, setClock] = useState("");

  useEffect(() => {
    const savedPrefs = JSON.parse(localStorage.getItem("prefs"));
    if (savedPrefs) {
      setDarkMode(savedPrefs.darkMode);
      setLargeText(savedPrefs.largeText);
    }
    const updateClock = () => {
      const now = new Date();
      const time = now.toLocaleTimeString("he-IL", { hour: "2-digit", minute: "2-digit" });
      const date = now.toLocaleDateString("he-IL", { weekday: "long", year: "numeric", month: "long", day: "numeric" });
      setClock(`${date} • ${time}`);
    };
    updateClock();
    const interval = setInterval(updateClock, 60000);
    return () => clearInterval(interval);
  }, []);

  const saveSettings = () => {
    localStorage.setItem("prefs", JSON.stringify({ darkMode, largeText }));
    setShowSettings(false);
  };

  const kupot = [
    { name: "כללית", url: "https://e-services.clalit.co.il/onlinewebquick/%D7%96%D7%9E%D7%9F_%D7%AA%D7%95%D7%A8" },
    { name: "מכבי", url: "https://www.maccabi4u.co.il/14-he/Maccabi.aspx" },
    { name: "לאומית", url: "https://home.leumit.co.il/" }
  ];

  const taxiApps = [
    { name: "Gett", url: "https://gett.com/il/" },
    { name: "Yango", url: "https://yango.com/he_il/" }
  ];

  // background & theme
  const main = `min-h-screen flex flex-col items-center px-4 py-8 gap-6 text-right ${
    darkMode
      ? "bg-gradient-to-br from-slate-900 via-slate-950 to-black text-white"
      : "bg-gradient-to-br from-indigo-50 via-white to-sky-50 text-slate-800"
  } ${largeText ? "text-xl" : "text-base"}`;

  const card = `${darkMode ? "bg-white/10" : "bg-white/80"} backdrop-blur-md w-full max-w-xl rounded-[24px] shadow-[0_8px_20px_rgba(0,0,0,0.12)] p-6 border ${
    darkMode ? "border-white/10" : "border-white/60"
  }`;

  const iosTileBase = `${darkMode ? "bg-white/10" : "bg-white"} flex items-center gap-4 rounded-[24px] p-4 shadow-[0_8px_20px_rgba(0,0,0,0.08)] border ${
    darkMode ? "border-white/10" : "border-slate-200"
  }`;

  const iosIconWrap = (from, to) => `w-16 h-16 rounded-2xl text-white grid place-items-center bg-gradient-to-br ${from} ${to}`;
  const menuBox = `absolute top-full right-0 mt-2 rounded-xl shadow-lg border overflow-hidden w-full ${
    darkMode ? "bg-slate-800/95 border-white/10" : "bg-white border-slate-200"
  }`;

  return (
    <div className={main}>
      {/* Clock */}
      <div className={`text-sm ${darkMode ? "text-slate-400" : "text-slate-500"}`}>{clock}</div>

      {/* Header */}
      <div className={`${card} text-center`}>
        <h1 className={`text-3xl md:text-4xl font-bold ${darkMode ? "text-indigo-300" : "text-indigo-700"}`}>ברוכים הבאים ל״נכד״</h1>
        <p className={`${darkMode ? "text-slate-400" : "text-slate-500"} mt-1`}>שירותים יומיומיים – בלחיצה אחת</p>
      </div>

      {/* Services */}
      <div className={`${card} grid sm:grid-cols-1 gap-4`}>
        {/* Emergency */}
        <div className="relative">
          <button onClick={() => setShowEmergency(!showEmergency)} className={iosTileBase}>
            <div className={iosIconWrap("from-rose-400", "to-rose-600")}><IconSiren /></div>
            <div className="flex-1">
              <div className="font-semibold">חירום</div>
              <div className={`${darkMode ? "text-slate-400" : "text-slate-500"} text-xs`}>משטרה · מד״א · כיבוי</div>
            </div>
          </button>
          {showEmergency && (
            <div className={menuBox}>
              <a href="tel:100" className={`block px-4 py-3 ${darkMode ? "hover:bg-white/5" : "hover:bg-slate-50"}`}>🚔 משטרה — 100</a>
              <a href="tel:101" className={`block px-4 py-3 ${darkMode ? "hover:bg-white/5" : "hover:bg-slate-50"}`}>🚑 מד״א — 101</a>
              <a href="tel:102" className={`block px-4 py-3 ${darkMode ? "hover:bg-white/5" : "hover:bg-slate-50"}`}>🔥 כיבוי אש — 102</a>
            </div>
          )}
        </div>

        {/* Doctor */}
        <div className="relative">
          <button onClick={() => setShowKupot(!showKupot)} className={iosTileBase}>
            <div className={iosIconWrap("from-emerald-400", "to-emerald-600")}><IconStetho /></div>
            <div className="flex-1">
              <div className="font-semibold">קבע תור לרופא</div>
              <div className={`${darkMode ? "text-slate-400" : "text-slate-500"} text-xs`}>כללית · מכבי · לאומית</div>
            </div>
          </button>
          {showKupot && (
            <div className={menuBox}>
              {kupot.map((k, i) => (
                <a key={i} href={k.url} target="_blank" rel="noreferrer" className={`block px-4 py-3 ${darkMode ? "hover:bg-white/5" : "hover:bg-slate-50"}`}>
                  🏥 {k.name}
                </a>
              ))}
            </div>
          )}
        </div>

        {/* Taxi */}
        <div className="relative">
          <button onClick={() => setShowTaxi(!showTaxi)} className={iosTileBase}>
            <div className={iosIconWrap("from-amber-400", "to-amber-600")}><IconTaxi /></div>
            <div className="flex-1">
              <div className="font-semibold">הזמן מונית</div>
              <div className={`${darkMode ? "text-slate-400" : "text-slate-500"} text-xs`}>Gett · Yango</div>
            </div>
          </button>
          {showTaxi && (
            <div className={menuBox}>
              {taxiApps.map((a, i) => (
                <a key={i} href={a.url} target="_blank" rel="noreferrer" className={`block px-4 py-3 ${darkMode ? "hover:bg-white/5" : "hover:bg-slate-50"}`}>
                  🚕 {a.name}
                </a>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Help */}
      <div id="help" className={card}>
        <div className={`flex items-center gap-2 ${darkMode ? "text-pink-300" : "text-pink-700"} font-semibold text-lg mb-2`}>🆘 עזרה</div>
        <ul className={`list-disc pr-5 space-y-1 text-sm ${darkMode ? "text-slate-300" : "text-slate-700"}`}>
          <li>לחצו על כפתורי השירותים – כל כפתור מוביל לאתר או פעולה.</li>
          <li>השתמשו בהגדרות כדי להגדיל טקסט או להדליק מצב כהה.</li>
          <li>אם עדיין קשה, בקשו עזרה מקרוב משפחה.</li>
        </ul>
      </div>

      {/* Settings trigger */}
      <button onClick={() => setShowSettings(true)} className={`underline ${darkMode ? "text-indigo-300" : "text-indigo-700"} text-sm`}>⚙️ הגדרות נגישות</button>

      {/* Settings modal */}
      {showSettings && (
        <div className="fixed inset-0 bg-black/50 grid place-items-center p-4 z-50">
          <div className={`${darkMode ? "bg-slate-900 text-white border border-white/10" : "bg-white"} rounded-[24px] shadow-[0_8px_20px_rgba(0,0,0,0.12)] w-full max-w-md p-6 space-y-4 text-right`}>
            <h2 className="text-xl font-bold">⚙️ הגדרות נגישות</h2>
            <label className="flex items-center gap-2">
              <input type="checkbox" checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
              <span>מצב כהה</span>
            </label>
            <label className="flex items-center gap-2">
              <input type="checkbox" checked={largeText} onChange={() => setLargeText(!largeText)} />
              <span>הגדלת טקסט</span>
            </label>
            <div className="flex justify-end gap-2 pt-2">
              <button onClick={() => setShowSettings(false)} className={`${darkMode ? "bg-white/10" : "bg-slate-200"} px-4 py-2 rounded-md`}>ביטול</button>
              <button onClick={saveSettings} className="px-4 py-2 rounded-md bg-indigo-600 text-white">שמור</button>
            </div>
          </div>
        </div>
      )}

      <p className={`${darkMode ? "text-slate-500" : "text-slate-400"} text-xs`}>שירות פשוט. בעברית. בשבילך.</p>
    </div>
  );
}
