import React, { useState, useEffect } from "react";

export default function Home() {
  const [showEmergency, setShowEmergency] = useState(false);
  const [showKupot, setShowKupot] = useState(false);
  const [showTaxi, setShowTaxi] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [largeText, setLargeText] = useState(false);

  useEffect(() => {
    const savedPrefs = JSON.parse(localStorage.getItem("prefs"));
    if (savedPrefs) {
      setDarkMode(savedPrefs.darkMode);
      setLargeText(savedPrefs.largeText);
    }
  }, []);

  const saveSettings = () => {
    localStorage.setItem("prefs", JSON.stringify({ darkMode, largeText }));
    setShowSettings(false);
  };

  const kupot = [
    { name: "כללית", url: "https://e-services.clalit.co.il/onlinewebquick/%D7%96%D7%9E%D7%9F_%D7%AA%D7%95%D7%A8" },
    { name: "מכבי", url: "https://www.maccabi4u.co.il/14-he/Maccabi.aspx" },
    { name: "לאומית", url: "https://home.leumit.co.il/" }
  ];

  const taxiApps = [
    { name: "Gett", url: "https://gett.com/il/" },
    { name: "Yango", url: "https://yango.com/he_il/" }
  ];

  const baseClasses = "min-h-screen flex flex-col items-center justify-start p-6 space-y-6 text-right transition-colors duration-300";
  const themeClasses = darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900";
  const textSize = largeText ? "text-xl" : "text-base";
  const mainClass = baseClasses + " " + themeClasses + " " + textSize;

  return (
    <div className={mainClass}>
      <h1 className="text-2xl md:text-3xl font-bold mt-4">ברוכים הבאים ל"נכד" 👋</h1>

      <nav className="w-full max-w-md flex justify-around text-sm underline text-blue-700">
        <a href="#services">שירותים</a>
        <a href="#help">עזרה</a>
        <button onClick={() => setShowSettings(true)}>⚙️ הגדרות</button>
      </nav>

      <div id="services" className="grid sm:grid-cols-1 md:grid-cols-2 gap-4 w-full max-w-md text-center mt-4">
        <div className="relative col-span-1">
          <button
            onClick={() => setShowEmergency(!showEmergency)}
            className="bg-red-500 hover:bg-red-600 text-white py-4 px-2 rounded-2xl text-lg md:text-xl shadow w-full"
          >
            🚑 חירום
          </button>
          {showEmergency && (
            <div className="absolute top-full right-0 mt-2 bg-white border rounded-xl shadow-lg w-full z-10 text-right">
              <a href="tel:100" className="block text-right px-3 md:px-4 py-1.5 md:py-2 hover:bg-gray-100">🚔 משטרה - 100</a>
              <a href="tel:101" className="block text-right px-3 md:px-4 py-1.5 md:py-2 hover:bg-gray-100">🚑 מד"א - 101</a>
              <a href="tel:102" className="block text-right px-3 md:px-4 py-1.5 md:py-2 hover:bg-gray-100">🔥 כיבוי אש - 102</a>
            </div>
          )}
        </div>

        <div className="relative col-span-1">
          <button
            onClick={() => setShowKupot(!showKupot)}
            className="bg-green-500 hover:bg-green-600 text-white py-4 px-2 rounded-2xl text-lg md:text-xl shadow w-full"
          >
            🩺 קבע תור לרופא
          </button>
          {showKupot && (
            <div className="absolute top-full right-0 mt-2 bg-white border rounded-xl shadow-lg w-full z-10">
              {kupot.map((kupa, idx) => (
                <a key={idx} href={kupa.url} target="_blank" rel="noopener noreferrer" className="block text-right px-3 md:px-4 py-1.5 md:py-2 hover:bg-gray-100">
                  🏥 {kupa.name}
                </a>
              ))}
            </div>
          )}
        </div>

        <div className="relative col-span-1">
          <button
            onClick={() => setShowTaxi(!showTaxi)}
            className="bg-yellow-500 hover:bg-yellow-600 text-white py-4 px-2 rounded-2xl text-lg md:text-xl shadow w-full"
          >
            🚖 הזמן מונית
          </button>
          {showTaxi && (
            <div className="absolute top-full right-0 mt-2 bg-white border rounded-xl shadow-lg w-full z-10">
              {taxiApps.map((app, idx) => (
                <a key={idx} href={app.url} target="_blank" rel="noopener noreferrer" className="block text-right px-3 md:px-4 py-1.5 md:py-2 hover:bg-gray-100">
                  🚕 {app.name}
                </a>
              ))}
            </div>
          )}
        </div>

        <button className="bg-purple-500 hover:bg-purple-600 text-white py-4 px-2 rounded-2xl text-lg md:text-xl shadow col-span-2">
          📅 קבע פגישה
        </button>
      </div>

      {showSettings && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white p-6 rounded-xl w-full sm:max-w-sm md:max-w-md text-right space-y-4 shadow-lg">
            <h2 className="text-xl font-bold">⚙️ הגדרות נגישות</h2>
            <div className="space-y-2">
              <label className="flex items-center space-x-2">
                <input type="checkbox" checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
                <span>מצב כהה</span>
              </label>
              <label className="flex items-center space-x-2">
                <input type="checkbox" checked={largeText} onChange={() => setLargeText(!largeText)} />
                <span>הגדלת טקסט</span>
              </label>
            </div>
            <div className="flex justify-end gap-2">
              <button onClick={() => setShowSettings(false)} className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400">ביטול</button>
              <button onClick={saveSettings} className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700">שמור</button>
            </div>
          </div>
        </div>
      )}

      <div id="help" className="w-full max-w-md bg-white rounded-2xl border p-4 shadow text-right">
        <h2 className="text-xl font-semibold mb-2">🆘 עזרה</h2>
        <p className="mb-2">אם אתם מתקשים להשתמש באתר, נסו את אחד מהפתרונות הבאים:</p>
        <ul className="list-disc pr-5 space-y-1 text-sm">
          <li>לחצו על כפתורי השירותים – כל כפתור מוביל לאתר או פעולה.</li>
          <li>השתמשו בהגדרות כדי להגדיל טקסט או להדליק מצב כהה.</li>
          <li>אם עדיין קשה, בקשו עזרה מקרוב משפחה.</li>
        </ul>
      </div>

      <p className="text-gray-600 text-sm mt-8">שירות פשוט. בעברית. בשבילך.</p>
    </div>
  );
}
